    query post($relativePath: String!) {
  post(relativePath: $relativePath) {
    ... on Document {
      _sys {
        filename
        basename
        breadcrumbs
        path
        relativePath
        extension
      }
      id
    }
    ...PostParts
  }
}

    fragment PostParts on Post {
  __typename
  title
  description
  date
  tags
  imageUrl
  body
}

